package basic.concepts;

public class Car_SwapValue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Swapping the value
		
			int a=10;
			int b=20;
			int c;
				
			System.out.println("Before Swapping the vale: "+a+" " +b);
			c=a;
			a=b;
			b=c;
				
			System.out.println("After Swapping the vale: "+a+" " +b);


	}

}
