package basic.concepts;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/**
		 * Basics of DataTypes:
		 */
		//1. int:
		int i=10; //Defined int i and assigned the value 10
		System.out.println("Value of int is: " +i);
		i=20; //Changing the value of i
		System.out.println("Value of int is: " +i);
		int j=25;
		System.out.println("Value of int is:" +j);
		
		//2. Double:
		double d=10.25;
		System.out.println("Value of double is: " +d);
		
		//3. Float:
		float f=15.25f;
		System.out.println("Value of float is: " +f);
		
		//4. Character
		char c='A';
		System.out.println("Value of character is: " +c);
		
		//5.String:
		String s="Java";
		System.out.println("Value of string is: " +s);
		
		//6. Boolean 
		boolean b1=true;
		boolean b2=false;
		System.out.println("Value of b1 is: " +b1);
		System.out.println("Value of b2 is: " +b2);
	}
}
